import React, { useState } from 'react';
import './Form.css';
import { Link } from 'react-router-dom'

const Form = () => {

  return (
    <>
      <div className='form-container'>
        <span className='close-btn'><Link to = "/">x</Link></span>
        <div className='form-content-left'>
          <img className='form-img' src='img/img-2.svg' alt='spaceship' />
        </div>
      </div>
      <div className='form-content-right'>
      <form onSubmit={handleSubmit} className='form' noValidate>
        <h1>
          Get started with us today! Create your account by filling out the
          information below.
        </h1>

        {/* Here in this whole page we are creating the right side entry boxes and headings with added validations on them  */}
            {/* Username label and entry box */}
        
        <div className='form-inputs'>
          <label className='form-label'>Username</label>   
          <input
            className='form-input'
            type='text'
            name='username'
            placeholder='Enter your username'
            value={values.username}
            onChange={handleChange}
          />
          {errors.username && <p>{errors.username}</p>}
        </div>

          {/* Email label and entry box */}

        <div className='form-inputs'>
          <label className='form-label'>Email</label>
          <input
            className='form-input'
            type='email'
            name='email'
            placeholder='Enter your email'
            value={values.email}
            onChange={handleChange}
          />
          {errors.email && <p>{errors.email}</p>}
        </div>

         {/*  password label and entry box */}

        <div className='form-inputs'>
          <label className='form-label'>Password</label>
          <input
            className='form-input'
            type='password'
            name='password'
            placeholder='Enter your password'
            value={values.password}
            onChange={handleChange}
          />
          {errors.password && <p>{errors.password}</p>}
        </div>
        <div className='form-inputs'>
          <label className='form-label'>Address</label>   
          <input
            className='form-input'
            type='text'
            name='address'
            placeholder='Enter your Address'
            value={values.address}
            onChange={handleChange}
          />
          {errors.username && <p>{errors.username}</p>}
        </div>
        <div className='form-inputs'>
          <label className='form-label'>Contact No</label>   
          <input
            className='form-input'
            type='text'
            name='Contact No'
            placeholder='Enter your contact'
            value={values.contact}
            onChange={handleChange}
          />
          {errors.username && <p>{errors.username}</p>}
        </div>

         {/*Confirm password label and entry box */}

        <div className='form-inputs'>
          <label className='form-label'>Confirm Password</label>
          <input
            className='form-input'
            type='password'
            name='password2'
            placeholder='Confirm your password'
            value={values.password2}
            onChange={handleChange}
          />
          {errors.password2 && <p>{errors.password2}</p>}
        </div>

        {/*Sign up label and entry box */}

        <button className='form-input-btn' type='submit'>
          Sign up
        </button>

        {/* added a login page link */}

        <span className='form-input-login'>
          Already have an account? Login <a href='Login'>here</a>
        </span>
      </form>
    </div>
  );
};
    </>
  );
};

export default Form;
