import React, {useState} from 'react'
import Header from '../layout/Header/Header';
import 'bootstrap/dist/css/bootstrap.css';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import {useNavigate} from 'react-router-dom'
const Form1 = () => {

  const [credentials, setCredentials] = useState({username: "", email: "", address: "", contact: "", typeLeave: "", startdate:"", enddate:""}) 
  let history = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    const {username, email, address, contact, typeLeave, startdate, enddate} = credentials;
    console.log(credentials)
    const response = await fetch("http://localhost:5000/leaveapi/leaveapi", {
      
        method: 'POST',
        headers: {
            'Content-Type': 'application/json'
        },
        body: JSON.stringify({username: username, email: email, address: address, contact: contact, typeLeave: typeLeave, startdate: startdate, enddate: enddate})
    });
    console.log(response);
    const json = await response.json();
    console.log(json);
    if (json.success){
        // redirect
        history("/viewLeave");

    }
    else{
        alert("Invalid credentials");
    }
}

const onChange = (e)=>{
    setCredentials({...credentials, [e.target.name]: e.target.value})
} 

    return (
        <div>
          <Header/>
          <div style={{ display: 'block', 
                  width: 700, 
                  padding: 30 }}>
      <h4>LEAVE APPLICATION FORM</h4>
      <Form onSubmit={handleSubmit}>
      <Form.Group>

          <Form.Label>ENTER YOUR FULL NAME:</Form.Label>
          <Form.Control type="text" 
                        placeholder="Enter your full name" name="name" id ="name"/>
        </Form.Group>
        <Form.Group>
          <Form.Label>ENTER YOUR EMAIL:</Form.Label>
          <Form.Control type="email" 
                        placeholder="Enter your your email address" name="email" id ="email" onChange={onChange} />
        </Form.Group>
        <Form.Group>
          <Form.Label>ENTER YOUR ADDRESS:</Form.Label>
          <Form.Control type="text" placeholder="Enter your address" name="address" id ="address" onChange={onChange}/>
        </Form.Group>
        <Form.Group>
          <Form.Label>ENTER YOUR CONTACT</Form.Label>
          <Form.Control type="tel" placeholder="Enter your contact" name="contact" id ="contact" onChange={onChange}/>
        </Form.Group>
        <Form.Label>TYPE OF LEAVE</Form.Label>
        <Form.Select aria-label="Default select example" name="typeleave" id="typeleave" onChange={onChange}>
              <option>Select Type of Leave</option>
              <option value="1">CASUAL LEAVE</option>
              <option value="2">PAID LEAVE</option>
              <option value="3">MATERNITY LEAVE</option>
              <option value="3">PATERNITY LEAVE</option>
            </Form.Select>
        <Form.Group controlId="dob">
                            <Form.Label>START DATE</Form.Label>
                            <Form.Control type="date" name="startdate" controlid="startdate" placeholder="Start Date" onChange={onChange}/>
                        </Form.Group>
                        <Form.Group controlId="dob">
                            <Form.Label>END DATE</Form.Label>
                            <Form.Control type="date" name="enddate" controlid="enddate" placeholder="End Date" onChange={onChange} />
                        </Form.Group>
        <br/>              
        <Button variant="primary" type="submit">
           Send Request
        </Button>
      </Form>
    </div>
          
        
            
        </div>
    )
}

export default Form1
