import React, {useState} from 'react'
import './Login.css'
import Loginpic from '../../resources/a.png';
import ID from '../../resources/ID.png';
import Password from '../../resources/pass.png';
import {useHistory} from 'react-router-dom'

const Login = () =>{

    const [credentials, setCredentials] = useState({email: "", password: ""}) 
    let history = useHistory();

    const handleSubmit = async (e) => {
        e.preventDefault();
        const response = await fetch("http://localhost:5000/api/auth/login", {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({email: credentials.email, password: credentials.password})
        });
        const json = await response.json()
        console.log(json);
        if (json.success){
            // Save the auth token and redirect
            localStorage.setItem('token', json.authtoken); 
            history.push("/");

        }
        else{
            alert("Invalid credentials");
        }
    }

    const onChange = (e)=>{
        setCredentials({...credentials, [e.target.name]: e.target.value})
    }

    

  return (
    
        <div className='main'>
            <div className='sub-main'>
                <div>
                    <div className='imgs'>
                        <div className='container-image'>
                            <img src={Loginpic} alt="profile-pic" className='profile'/>
                        </div>
                    </div>

                    <div>
                        <h1> MANAGER LOGIN </h1>
                        <br></br>
                        <form>
                        <div>
                            <img src={ID} alt="ID" className='ID'/>
                            <input type="text" placeholder="Username" value={credentials.username} onChange={onChange} className="name" id="username" name="username" />    
                        </div>
                        <div className='second'>
                            <img src={Password} alt="PASS" className='PASS'/>
                            <input type="Password" placeholder="Password" value={credentials.password} onChange={onChange} className="name" id="password" name="password"/>    
                        </div>

                        <div className='login-button'>
                            <button onSubmit={handlesubmit}> LOGIN </button>
                        </div>
                        </form>
                            <p className='link'>
                                Forgot Password ? then <a href='Form'>Sign-up</a>
                            </p>
                    </div>
                </div>
            </div>
        </div>
  )
}

export default Login
