import React, {useState} from 'react';
import './Form.css';
import { Link } from 'react-router-dom'
import {useNavigate} from 'react-router-dom'


const Form = () => {

  const [credentials, setCredentials] = useState({username: "", password: ""}) 
    let history = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    const response = await fetch("http://localhost:5000/api/auth/login", {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json'
        },
        body: JSON.stringify({username: credentials.username, password: credentials.password})
    });
    const json = await response.json()
    console.log(json);
    if (json.success){
        // Save the auth token and redirect
        localStorage.setItem('token', json.authtoken); 
        history("/profiler");

    }
    else{
        alert("Invalid credentials");
    }
}

const onChange = (e)=>{
    setCredentials({...credentials, [e.target.username]: e.target.value})
} 
  return (
      <div className='form-container'>
        <span className='close-btn'><Link to = "/">x</Link></span>
        <div className='form-content-left'>
          <img className='form-img' src='img/img-2.svg' alt='spaceship' />
        </div>

      <div className='form-content-right'>
      
        
        <form className='form'>
        <h1>
          Get started with us today! Create your account by filling out the
          information below.
        </h1>
        
        <div className='form-inputs'>
          <label className='form-label'>Username</label>   
          <input
            className='form-input'
            type='text'
            name='username'
            placeholder='Enter your name'
            id='username' onChange={onChange}
          />
        </div>

        <div className='form-inputs'>
          <label className='form-label'>Email</label>
          <input
            className='form-input'
            type='email'
            name='email'
            placeholder='Enter your email'
            id='email' onChange={onChange}  
          />
          </div>

        <div className='form-inputs'>
          <label className='form-label'>Password</label>
          <input
            className='form-input'
            type='password'
            name='password'
            placeholder='Enter your password'
            id='password' onChange={onChange}
          />
        </div>

        <div className='form-inputs'>
          <label className='form-label'>Address</label>   
          <input
            className='form-input'
            type='text'
            name='address'
            placeholder='Enter your Address'
            id='address' onChange={onChange}
          />
        </div>

        <div className='form-inputs'>
          <label className='form-label'>Contact No</label>   
          <input
            className='form-input'
            type='text'
            name='Contact_no'
            placeholder='Enter your contact'
            id='contact_no' onChange={onChange}
          />
        </div>

        <div className='form-inputs'>
          <label className='form-label'>Confirm Password</label>
          <input
            className='form-input'
            type='password'
            name='password2'
            placeholder='Confirm your password'
            id='password2' onChange={onChange}
          />
        </div>

        <button className='form-input-btn' type='submit'>Sign up</button>
        
        <span className='form-input-login'> Already have an account? Login <a href='Login'>here</a></span>

        
      </form>
      </div>
      </div>
  );
};

export default Form;
